const modals=document.getElementsByClassName("modalBtn");for(let i=0;i<modals.length;i++){modals[i].addEventListener("click",function(){let forModal=this.getAttribute('data-for');document.getElementById(forModal).showModal();document.getElementById(forModal).style.display="flex";let elems=document.getElementById(forModal).getElementsByTagName("*");for(let i=0;i<elems.length;i++){if(elems[i].getAttribute('data-close')){elems[i].addEventListener("click",function(){document.getElementById(forModal).close();document.getElementById(forModal).style.display="none"})}}})}